import React from "react";
import {
  Container,
  Typography,
  Box,
  Chip,
  Hidden,
  Divider,
} from "@material-ui/core";
import { useParams, useHistory } from "react-router-dom";
import { LogoLink } from "../components/logo/LogoLink";
import { ThemeToggle } from "../components/theme/ThemeToggle";
import { FooterText } from "../components/footer/FooterText";
import { SocialIcons } from "../components/content/SocialIcons";
import { NavigationButtons } from "../components/navigation/NavigationButtons";
import DisplacementSphere from "../components/background/DisplacementSphere";
import { makeStyles } from "@material-ui/core/styles";
import { TextDecrypt } from "../components/content/TextDecrypt";
import { blogPosts } from "../data/blogPosts";

const useStyles = makeStyles((theme) => ({
  root: {
    minHeight: "100vh",
    display: "flex",
    flexDirection: "column",
    position: "relative",
  },
  content: {
    marginTop: "auto",
    marginBottom: "auto",
    paddingTop: theme.spacing(8),
    paddingBottom: theme.spacing(8),
    zIndex: 1,
  },
  article: {
    backgroundColor: "rgba(255, 255, 255, 0.05)",
    backdropFilter: "blur(10px)",
    border: `1px solid ${theme.palette.divider}`,
    borderRadius: theme.spacing(2),
    padding: theme.spacing(4),
    marginTop: theme.spacing(4),
    [theme.breakpoints.down("sm")]: {
      padding: theme.spacing(3),
    },
  },
  header: {
    marginBottom: theme.spacing(4),
  },
  title: {
    fontWeight: 700,
    marginBottom: theme.spacing(3),
    lineHeight: 1.3,
  },
  meta: {
    display: "flex",
    alignItems: "center",
    gap: theme.spacing(2),
    marginBottom: theme.spacing(3),
    flexWrap: "wrap",
  },
  backButton: {
    color: theme.palette.primary.main,
    cursor: "pointer",
    fontWeight: 600,
    display: "inline-flex",
    alignItems: "center",
    marginBottom: theme.spacing(3),
    "&:hover": {
      textDecoration: "underline",
    },
    "&:before": {
      content: '"← "',
      marginRight: theme.spacing(1),
    },
  },
  content_text: {
    lineHeight: 1.8,
    fontSize: "1.1rem",
    "& p": {
      marginBottom: theme.spacing(3),
    },
    "& h2": {
      marginTop: theme.spacing(4),
      marginBottom: theme.spacing(2),
      fontWeight: 600,
    },
    "& h3": {
      marginTop: theme.spacing(3),
      marginBottom: theme.spacing(2),
      fontWeight: 600,
    },
    "& code": {
      backgroundColor: "rgba(0, 0, 0, 0.2)",
      padding: "2px 6px",
      borderRadius: "4px",
      fontFamily: "monospace",
      fontSize: "0.9em",
    },
    "& pre": {
      backgroundColor: "rgba(0, 0, 0, 0.3)",
      padding: theme.spacing(2),
      borderRadius: theme.spacing(1),
      overflowX: "auto",
      marginBottom: theme.spacing(3),
      "& code": {
        backgroundColor: "transparent",
        padding: 0,
      },
    },
    "& ul, & ol": {
      marginBottom: theme.spacing(3),
      paddingLeft: theme.spacing(4),
    },
    "& li": {
      marginBottom: theme.spacing(1),
    },
    "& blockquote": {
      borderLeft: `4px solid ${theme.palette.primary.main}`,
      paddingLeft: theme.spacing(2),
      marginLeft: 0,
      marginBottom: theme.spacing(3),
      fontStyle: "italic",
      color: theme.palette.text.secondary,
    },
  },
  tagContainer: {
    display: "flex",
    flexWrap: "wrap",
    gap: theme.spacing(1),
  },
  chip: {
    fontSize: "0.75rem",
    height: "24px",
  },
}));

export const BlogPost = () => {
  const classes = useStyles();
  const { slug } = useParams();
  const history = useHistory();
  const post = blogPosts[slug];

  if (!post) {
    history.push("/blogs");
    return null;
  }

  const handleBack = () => {
    history.push("/blogs");
  };

  return (
    <>
      <div className={classes.root}>
        <DisplacementSphere />
        <LogoLink />
        <ThemeToggle />
        <Hidden smDown>
          <SocialIcons />
          <NavigationButtons />
        </Hidden>

        <Container className={classes.content} maxWidth="md">
          <Typography className={classes.backButton} onClick={handleBack}>
            Back to Blogs
          </Typography>

          <Box className={classes.article}>
            <Box className={classes.header}>
              <Typography variant="h3" component="h1" className={classes.title}>
                <TextDecrypt text={post.title} />
              </Typography>

              <Box className={classes.meta}>
                <Chip
                  label={post.date}
                  size="small"
                  className={classes.chip}
                  variant="outlined"
                />
                <Chip
                  label={post.readTime}
                  size="small"
                  className={classes.chip}
                  variant="outlined"
                />
                <Typography variant="body2" color="textSecondary">
                  By {post.author}
                </Typography>
              </Box>

              <Box className={classes.tagContainer}>
                {post.tags.map((tag, index) => (
                  <Chip
                    key={index}
                    label={tag}
                    size="small"
                    className={classes.chip}
                    color="primary"
                    variant="outlined"
                  />
                ))}
              </Box>
            </Box>

            <Divider />

            <Box className={classes.content_text}>
              {post.content.split("\n\n").map((paragraph, index) => {
                // Handle headings
                if (paragraph.startsWith("## ")) {
                  return (
                    <Typography key={index} variant="h4" component="h2">
                      {paragraph.replace("## ", "")}
                    </Typography>
                  );
                }
                if (paragraph.startsWith("### ")) {
                  return (
                    <Typography key={index} variant="h5" component="h3">
                      {paragraph.replace("### ", "")}
                    </Typography>
                  );
                }
                // Handle code blocks
                if (paragraph.startsWith("```")) {
                  const code = paragraph
                    .replace(/```javascript\n?/, "")
                    .replace(/```\n?$/, "");
                  return (
                    <pre key={index}>
                      <code>{code}</code>
                    </pre>
                  );
                }
                // Handle blockquotes
                if (paragraph.startsWith("> ")) {
                  return (
                    <blockquote key={index}>
                      <Typography variant="body1">
                        {paragraph.replace(/^> \*\*(.+?)\*\*: /, "")}
                      </Typography>
                    </blockquote>
                  );
                }
                // Handle lists
                if (paragraph.includes("\n- ") || paragraph.includes("\n1. ")) {
                  const items = paragraph
                    .split("\n")
                    .filter((line) => line.trim());
                  const isOrdered = items[0]?.match(/^\d+\./);
                  const ListTag = isOrdered ? "ol" : "ul";

                  return (
                    <ListTag key={index}>
                      {items.map((item, i) => {
                        const text = item
                          .replace(/^- /, "")
                          .replace(/^\d+\.\s/, "")
                          .replace(/\*\*(.+?)\*\*/g, "<strong>$1</strong>");
                        return (
                          <li
                            key={i}
                            dangerouslySetInnerHTML={{ __html: text }}
                          />
                        );
                      })}
                    </ListTag>
                  );
                }
                // Handle regular paragraphs with inline code
                if (paragraph.trim()) {
                  const formattedText = paragraph
                    .replace(/`([^`]+)`/g, "<code>$1</code>")
                    .replace(/\*\*(.+?)\*\*/g, "<strong>$1</strong>");

                  return (
                    <Typography
                      key={index}
                      variant="body1"
                      component="p"
                      dangerouslySetInnerHTML={{ __html: formattedText }}
                    />
                  );
                }
                return null;
              })}
            </Box>
          </Box>
        </Container>

        <FooterText />
      </div>
    </>
  );
};

export default BlogPost;
