import React from "react";
import {
  Container,
  Typography,
  Box,
  Card,
  CardContent,
  CardActionArea,
  Chip,
  Hidden,
} from "@material-ui/core";
import { LogoLink } from "../components/logo/LogoLink";
import { ThemeToggle } from "../components/theme/ThemeToggle";
import { FooterText } from "../components/footer/FooterText";
import { SocialIcons } from "../components/content/SocialIcons";
import { NavigationButtons } from "../components/navigation/NavigationButtons";
import DisplacementSphere from "../components/background/DisplacementSphere";
import { makeStyles } from "@material-ui/core/styles";
import { TextDecrypt } from "../components/content/TextDecrypt";

const useStyles = makeStyles((theme) => ({
  root: {
    minHeight: "100vh",
    display: "flex",
    flexDirection: "column",
    paddingTop: theme.spacing(8),
    paddingBottom: theme.spacing(4),
  },
  listItem: {
    transition: "background-color 0.3s ease",
    borderRadius: theme.spacing(1),
    marginBottom: theme.spacing(1),
    "&:hover": {
      backgroundColor: theme.palette.action.hover,
    },
  },
  header: {
    marginBottom: theme.spacing(4),
  },
  blogDate: {
    color: theme.palette.text.secondary,
    fontSize: "0.875rem",
    marginBottom: theme.spacing(1),
  },
}));

// Sample blog data - replace with your actual blog posts
const blogs = [
  {
    id: 1,
    title: "Getting Started with React Hooks",
    date: "October 15, 2025",
    excerpt:
      "Learn the fundamentals of React Hooks and how they can simplify your component logic. This comprehensive guide covers useState, useEffect, and custom hooks.",
    link: "#",
  },
  {
    id: 2,
    title: "Building Scalable Web Applications",
    date: "October 10, 2025",
    excerpt:
      "Best practices for creating web applications that scale. Explore architecture patterns, performance optimization, and modern development workflows.",
    link: "#",
  },
  {
    id: 3,
    title: "The Future of Web Development",
    date: "October 5, 2025",
    excerpt:
      "Exploring emerging trends and technologies shaping the future of web development. From AI integration to WebAssembly and beyond.",
    link: "#",
  },
  {
    id: 4,
    title: "Mastering CSS Grid and Flexbox",
    date: "September 28, 2025",
    excerpt:
      "A deep dive into modern CSS layout techniques. Learn when to use Grid vs Flexbox and create responsive designs with ease.",
    link: "#",
  },
];

export const Blogs = () => {
  const classes = useStyles();

  return (
    <>
      <LogoLink />
      <ThemeToggle />
      <Container className={classes.root} maxWidth="md">
        <Box className={classes.header}>
          <Typography variant="h2" component="h1" gutterBottom>
            Blog
          </Typography>
          <Typography variant="h6" color="textSecondary">
            Thoughts, tutorials, and insights on web development
          </Typography>
        </Box>

        <List>
          {blogs.map((blog, index) => (
            <React.Fragment key={blog.id}>
              <ListItem className={classes.listItem} alignItems="flex-start">
                <ListItemText
                  primary={
                    <Link
                      href={blog.link}
                      underline="hover"
                      color="inherit"
                      variant="h5"
                    >
                      {blog.title}
                    </Link>
                  }
                  secondary={
                    <>
                      <Typography className={classes.blogDate}>
                        {blog.date}
                      </Typography>
                      <Typography variant="body2" color="textSecondary">
                        {blog.excerpt}
                      </Typography>
                    </>
                  }
                />
              </ListItem>
              {index < blogs.length - 1 && <Divider component="li" />}
            </React.Fragment>
          ))}
        </List>
      </Container>
      <FooterText />
    </>
  );
};

export default Blogs;
